mapper.select:

         select  goodsid,goodsname,goodsteam,goodsprice
         from    (
                     select rownum as rnum, goodsid,goodsname,goodsteam,goodsprice
                     from (
                          SELECT goodsid,goodsname,goodsteam,goodsprice
						  FROM goods
                     )
                   where rownum <= #{endRowNo}
             )
            where rnum >= #{startRowNo}

SELECT goodsmaindata, goodsmaintype
FROM goods
WHERE goodsId = #{goodsId}
