mapper.delete:
DELETE
FROM goodsLike
WHERE userId = #{userId}
AND goodsId = #{goodsId}

mapper.select:
SELECT COUNT(*)
FROM goodsLike
WHERE userId = #{userId}
AND goodsId = #{goodsId}

SELECT count(*)
FROM goodsLike
WHERE userId = #{userId}


         SELECT  goodsid,goodsname,goodsteam,goodsprice, goodscategory, goodsstock, goodsstatus
         FROM    (
                     SELECT rownum as rnum, goodsid,goodsname,goodsteam,goodsprice, goodscategory, goodsstock, goodsstatus
                     FROM (
                          SELECT goodsid,goodsname,goodsteam,goodsprice, goodscategory, goodsstock, goodsstatus
						  FROM goods
						  WHERE goodsId IN (
						  	SELECT goodsId
							FROM goodsLike
							WHERE userId = #{userId}
						  )
                     )
                   WHERE rownum <= #{pager.endRowNo}
             )
            WHERE rnum >= #{pager.startRowNo}
